import java.util.Arrays;

public class Arraysort {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int arr [] = {10,2,3,4,5,6,7,8};
  
  for (int i = 0; i < arr.length; i++) {
	Arrays.sort(arr);
}
  
  System.out.print(Arrays.toString(arr));
	}

}
